import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

const initialValues = {
  full_name: "",
  email: "",
  birth_date: "",
  country_of_origin: "",
  terms_and_conditions: false,
};

const validationSchema = Yup.object({
  full_name: Yup.string().required("El nombre completo es requerido"),
  email: Yup.string()
    .email("Correo electrónico inválido")
    .required("El correo electrónico es requerido"),
  birth_date: Yup.date().required("La fecha de nacimiento es requerida"),
  country_of_origin: Yup.string().required("El país de origen es requerido"),
  terms_and_conditions: Yup.boolean().oneOf(
    [true],
    "Debe aceptar los términos y condiciones para continuar"
  ),
});

function DynamicForm({ formFields }) {
  const onSubmit = (values, { resetForm }) => {
    console.log(values);
    resetForm();
  };

  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={onSubmit}
    >
      {({ isSubmitting }) => (
        <Form>
          {formFields.map((field, index) => {
            switch (field.type) {
              case "text":
              case "email":
              case "date":
                return (
                  <div key={index}>
                    <label htmlFor={field.name}>{field.label}</label>
                    <Field
                      type={field.type}
                      id={field.name}
                      name={field.name}
                      required={field.required}
                    />
                    <ErrorMessage name={field.name} />
                  </div>
                );
              case "select":
                return (
                  <div key={index}>
                    <label htmlFor={field.name}>{field.label}</label>
                    <Field
                      as="select"
                      id={field.name}
                      name={field.name}
                      required={field.required}
                    >
                      <option value="">Seleccionar país</option>
                      {field.options.map((option, optionIndex) => (
                        <option key={optionIndex} value={option.value}>
                          {option.label}
                        </option>
                      ))}
                    </Field>
                    <ErrorMessage name={field.name} />
                  </div>
                );
              case "checkbox":
                return (
                  <div key={index}>
                    <Field
                      type={field.type}
                      id={field.name}
                      name={field.name}
                      required={field.required}
                    />
                    <label htmlFor={field.name}>{field.label}</label>
                    <ErrorMessage name={field.name} />
                  </div>
                );
              case "submit":
                return (
                  <div key={index}>
                    <button type="submit" disabled={isSubmitting}>
                      {field.label}
                    </button>
                  </div>
                );
              default:
                return null;
            }
          })}
        </Form>
      )}
    </Formik>
  );
}

export default DynamicForm;
